mapper.select:
select count(userid)
from user
where userid = #{userId}

select name, userid, email
from user
where userid = #{userId} and password = #{password}

select userid, password, name, email, phone
from user
order by userid;

select userid, password, name, email, phone
from user
where userid = #{userid}

mapper.update:
update user
password = #{password}, name = #{name}, email = #{email}, phone = #{phone},
where userid = #{userId}
